// Set the date 7 days from now
var countDownDate = new Date();
countDownDate.setDate(countDownDate.getDate() + 7);

// Update the countdown every second
var x = setInterval(function() {

  // Get the current date and time
  var now = new Date().getTime();
  
  // Calculate the remaining time
  var distance = countDownDate - now;
  
  // Calculate days, hours, minutes, and seconds
  var days = Math.floor(distance / (1000 * 60 * 60 * 24));
  var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  var seconds = Math.floor((distance % (1000 * 60)) / 1000);
  
  // Display the countdown in the HTML elements
  document.querySelector('.days').innerHTML = days;
  document.querySelector('.hours').innerHTML = hours;
  document.querySelector('.minutes').innerHTML = minutes;
  document.querySelector('.seconds').innerHTML = seconds;
  
  // If the countdown is finished, display a message
  if (distance < 0) {
    clearInterval(x);
    document.querySelector('.comsoon-title').innerHTML = "We're Here!";
    document.querySelector('.comsoon-text').innerHTML = "Our awesome site is now live!";
    document.getElementById('clockdiv').style.display = 'none';
  }
}, 1000);
